from django import formsfrom django.contrib.auth import get_user_modelUser = get_user_model()class SignupForm(forms.Form):    username = forms.CharField(        widget=forms.TextInput(            attrs={                'class': 'form-control',            }        )    )    password = forms.CharField(        widget=forms.PasswordInput(            attrs={                'class': 'form-control',            }        )    )    Confirm_Password = forms.CharField(        widget=forms.PasswordInput(            attrs={                'class': 'form-control',            }        )    )    # 아이디 사용중인가 확인검사    def clean_username(self):        username = self.cleaned_data['username']        if User.objects.filter(username=username).exists():            raise forms.ValidationError('아이디가 이미 사용중입니다.')        return username    # 비밀번호 1,2 가 일치하는지 유효성 검사    def clean_password2(self):        password = self.cleaned_data['password']        Confirm_Password = self.cleaned_data['Confirm_Password']        if password != Confirm_Password:            raise forms.ValidationError('비밀번호와 비밀번호 확인란의 값이 일치하지 않습니다.')    def signup(self):        if self.is_valid():            return User.objects.create_user(                username=self.cleaned_data['username'],                password=self.cleaned_data['Confirm_Password'],            )