from django.urls import pathfrom django.contrib.auth.views import LoginView, LogoutViewfrom . import viewsfrom django.conf import settingsurlpatterns = [    path('login/', LoginView.as_view(template_name = 'accounts/login_form.html'), name='login'),    path('signup/', views.signup, name='signup'),    # path('profile/'),    path('logout/', LogoutView.as_view(next_page=settings.LOGIN_URL), name='logout'),]