from django import formsfrom .models import About, Commentclass AboutForm(forms.ModelForm):    class Meta:        model = About        fields = ['title', 'country', 'city', 'content', 'photo']class CommentForm(forms.ModelForm):    class Meta:        model = Comment        fields = ['text']    def __init__(self, *args, **kwargs):        super().__init__(*args, **kwargs)        # self.fields['message'].label = ''        self.fields['text'].widget = forms.TextInput()        self.fields['text'].widget.attrs = {"class": "form-control", "placeholder": "댓글을 입력하세요."}