from django.urls import pathfrom . import viewsapp_name = 'blog'urlpatterns = [    path('', views.main_list, name='main'),    path('about/', views.about, name='about'),    path('about/new/', views.about_new, name='about_new'),  # 게시물 생성    path('detail/<int:pk>/', views.about_detail, name='about_detail'),  # 게시물 디테일    path('detail/<int:pk>/delete/', views.about_delete, name='about_delete'),  # 게시물 삭제    path('detail/<int:pk>/update/', views.about_update, name='about_update'),  # 게시물 수정    path('comment/create/<int:pk>/', views.comment_create, name='comment_create'),  # 댓글 생성    path('comment/update/<int:comment_pk>/', views.comment_update, name='comment_update'),  # 댓글 수정    path('comment/delete/<int:comment_pk>/', views.comment_delete, name='comment_delete'),  # 댓글 삭제    path('restaurant/', views.restaurant, name='restaurant'),    path('hotplace/', views.hotplace, name='hotplace'),]